from django import formsfrom django.core.exceptions import ValidationErrorfrom .models import *class ArticleForm(forms.ModelForm):    class Meta:        model = Article        fields = ['title', 'description', 'photo']        widgets = {            'title': forms.TextInput(attrs={'class': 'form-input'}),            'description': forms.Textarea(attrs={'class': 'text-area'}),        }    # This function clears the input field after the call and checks the input field for the number of    # characters    def clean_title(self):        title = self.cleaned_data['title']        if len(title) > 150:            raise ValidationError('Длина превышает 150 символов')        return title