from django.db import modelsfrom django.urls import reversefrom django.contrib.auth.models import Userclass Article(models.Model):    title = models.CharField(max_length=150, verbose_name="Заголовок")    # slug = models.SlugField(max_length=255, unique=True, db_index=True)    description = models.TextField(max_length=500, verbose_name="Текст статьи")    photo = models.ImageField(blank=True, upload_to="photos/%Y/%m/%d/", verbose_name="Фото")    time_create = models.DateTimeField(auto_now_add=True, verbose_name="Дата публикации статьи")    time_update = models.DateTimeField(auto_now=True, verbose_name="Дата редактирования статьи")    author = models.ForeignKey(User, on_delete=models.CASCADE)    def __str__(self):        return self.title    def get_absolute_url(self):        return reverse('article', kwargs={'article.pk': self.pk})    class Meta:        verbose_name = 'Список статей'        verbose_name_plural = 'Список статей'        ordering = ['-time_create', 'title']